import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CommentSection.css';

function CommentSection({ postId, boardId, isAnonymous }) {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newComment, setNewComment] = useState('');
  const [replyTo, setReplyTo] = useState(null);
  const [replyContent, setReplyContent] = useState('');

  useEffect(() => {
    fetchComments();
  }, [postId]);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`/ej2/api/comments/post/${postId}`);
      setComments(response.data);
      setLoading(false);
    } catch (error) {
      console.error('„Ç≥„É°„É≥„Éà„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      setComments([]);
      setLoading(false);
    }
  };

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      const commentData = {
        postId: parseInt(postId),
        userId: 1, // TODO: ÂÆüÈöõ„ÅÆ„É≠„Ç∞„Ç§„É≥„É¶„Éº„Ç∂„ÉºID
        content: newComment,
        anonymousId: isAnonymous ? `ÂåøÂêç${Math.floor(Math.random() * 1000)}` : null
      };

      await axios.post('/ej2/api/comments', commentData);
      setNewComment('');
      fetchComments();
    } catch (error) {
      console.error('„Ç≥„É°„É≥„Éà„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      alert('„Ç≥„É°„É≥„Éà„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const handleSubmitReply = async (e, parentId) => {
    e.preventDefault();
    if (!replyContent.trim()) return;

    try {
      const replyData = {
        postId: parseInt(postId),
        userId: 1, // TODO: ÂÆüÈöõ„ÅÆ„É≠„Ç∞„Ç§„É≥„É¶„Éº„Ç∂„ÉºID
        parentId: parentId,
        content: replyContent,
        anonymousId: isAnonymous ? `ÂåøÂêç${Math.floor(Math.random() * 1000)}` : null
      };

      await axios.post('/ej2/api/comments', replyData);
      setReplyContent('');
      setReplyTo(null);
      fetchComments();
    } catch (error) {
      console.error('Ëøî‰ø°„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      alert('Ëøî‰ø°„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const handleLikeComment = async (commentId) => {
    try {
      await axios.post(`/ej2/api/comments/${commentId}/like`);
      fetchComments();
    } catch (error) {
      console.error('„ÅÑ„ÅÑ„Å≠„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('„Ç≥„É°„É≥„Éà„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü')) return;

    try {
      await axios.delete(`/ej2/api/comments/${commentId}`);
      fetchComments();
    } catch (error) {
      console.error('„Ç≥„É°„É≥„Éà„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      alert('„Ç≥„É°„É≥„Éà„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const past = new Date(dateString);
    const diffInMinutes = Math.floor((now - past) / (1000 * 60));

    if (diffInMinutes < 1) return '„Åü„Å£„Åü‰ªä';
    if (diffInMinutes < 60) return `${diffInMinutes}ÂàÜÂâç`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}ÊôÇÈñìÂâç`;
    if (diffInMinutes < 10080) return `${Math.floor(diffInMinutes / 1440)}Êó•Ââç`;
    return past.toLocaleDateString('ja-JP');
  };

  const topLevelComments = comments.filter(c => !c.parentId);
  const getReplies = (parentId) => comments.filter(c => c.parentId === parentId);

  if (loading) {
    return <div className="comments-loading">„Ç≥„É°„É≥„Éà„ÇíË™≠„ÅøËæº„Åø‰∏≠...</div>;
  }

  return (
    <div className="comments-section">
      <div className="comments-header">
        <h3>„Ç≥„É°„É≥„Éà {comments.length}</h3>
      </div>

      <form className="comment-form" onSubmit={handleSubmitComment}>
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="„Ç≥„É°„É≥„Éà„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ..."
          rows="3"
        />
        <button type="submit" className="submit-comment-btn">
          „Ç≥„É°„É≥„Éà„ÇíÊäïÁ®ø
        </button>
      </form>

      <div className="comments-list">
        {topLevelComments.length === 0 ? (
          <div className="no-comments">
            ÊúÄÂàù„ÅÆ„Ç≥„É°„É≥„Éà„ÇíÊõ∏„ÅÑ„Å¶„Åø„Åæ„Åó„Çá„ÅÜÔºÅ
          </div>
        ) : (
          topLevelComments.map(comment => (
            <div key={comment.id} className="comment-item">
              <div className="comment-header">
                <span className="comment-author">
                  {isAnonymous ? comment.anonymousId || 'ÂåøÂêç' : `„É¶„Éº„Ç∂„Éº${comment.userId}`}
                </span>
                <span className="comment-time">{getTimeAgo(comment.createdAt)}</span>
              </div>

              <div className="comment-content">
                {comment.content}
              </div>

              <div className="comment-actions">
                <button
                  className="comment-action-btn"
                  onClick={() => handleLikeComment(comment.id)}
                >
                  üëç {comment.likeCount || 0}
                </button>
                <button
                  className="comment-action-btn"
                  onClick={() => setReplyTo(replyTo === comment.id ? null : comment.id)}
                >
                  üí¨ Ëøî‰ø°
                </button>
                <button
                  className="comment-action-btn delete"
                  onClick={() => handleDeleteComment(comment.id)}
                >
                  üóëÔ∏è ÂâäÈô§
                </button>
              </div>

              {replyTo === comment.id && (
                <form
                  className="reply-form"
                  onSubmit={(e) => handleSubmitReply(e, comment.id)}
                >
                  <textarea
                    value={replyContent}
                    onChange={(e) => setReplyContent(e.target.value)}
                    placeholder="Ëøî‰ø°„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ..."
                    rows="2"
                  />
                  <div className="reply-actions">
                    <button type="button" onClick={() => setReplyTo(null)}>
                      „Ç≠„É£„É≥„Çª„É´
                    </button>
                    <button type="submit">Ëøî‰ø°„ÇíÊäïÁ®ø</button>
                  </div>
                </form>
              )}

              {getReplies(comment.id).map(reply => (
                <div key={reply.id} className="comment-item reply">
                  <div className="comment-header">
                    <span className="comment-author">
                      {isAnonymous ? reply.anonymousId || 'ÂåøÂêç' : `„É¶„Éº„Ç∂„Éº${reply.userId}`}
                    </span>
                    <span className="comment-time">{getTimeAgo(reply.createdAt)}</span>
                  </div>

                  <div className="comment-content">
                    {reply.content}
                  </div>

                  <div className="comment-actions">
                    <button
                      className="comment-action-btn"
                      onClick={() => handleLikeComment(reply.id)}
                    >
                      üëç {reply.likeCount || 0}
                    </button>
                    <button
                      className="comment-action-btn delete"
                      onClick={() => handleDeleteComment(reply.id)}
                    >
                      üóëÔ∏è ÂâäÈô§
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default CommentSection;
