import React, { useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './PostWritePage.css';

function PostWritePage() {
  const { boardId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const board = location.state?.board;

  const [formData, setFormData] = useState({
    title: '',
    content: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Image upload state
  const [selectedImages, setSelectedImages] = useState([]);
  const [imagePreviewUrls, setImagePreviewUrls] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  // Handle image selection
  const handleImageSelect = (e) => {
    const files = Array.from(e.target.files);

    // Validate total count
    if (selectedImages.length + files.length > 5) {
      setError('ÁîªÂÉè„ÅØÊúÄÂ§ß5Êûö„Åæ„Åß„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åß„Åç„Åæ„Åô');
      return;
    }

    const validFiles = [];
    const validPreviews = [];

    for (const file of files) {
      // Validate file size (5MB max)
      if (file.size > 5 * 1024 * 1024) {
        setError(`${file.name} „ÅØ5MB„ÇíË∂Ö„Åà„Å¶„ÅÑ„Åæ„Åô`);
        continue;
      }

      // Validate file type
      const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];
      if (!validTypes.includes(file.type)) {
        setError(`${file.name} „ÅØÂØæÂøú„Åó„Å¶„ÅÑ„Å™„ÅÑ„Éï„Ç°„Ç§„É´ÂΩ¢Âºè„Åß„Åô`);
        continue;
      }

      validFiles.push(file);

      // Create preview URL
      const previewUrl = URL.createObjectURL(file);
      validPreviews.push(previewUrl);
    }

    // Update state
    setSelectedImages(prev => [...prev, ...validFiles]);
    setImagePreviewUrls(prev => [...prev, ...validPreviews]);
  };

  // Remove image
  const handleRemoveImage = (index) => {
    // Revoke object URL to free memory
    URL.revokeObjectURL(imagePreviewUrls[index]);

    setSelectedImages(prev => prev.filter((_, i) => i !== index));
    setImagePreviewUrls(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!formData.title.trim()) {
      setError('„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }
    if (!formData.content.trim()) {
      setError('ÂÜÖÂÆπ„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    // Get current user
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!user.id) {
      setError('„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô');
      navigate('/login');
      return;
    }

    setLoading(true);
    setError('');

    try {
      let imageUrls = [];

      // Upload images if there are any
      if (selectedImages.length > 0) {
        const imageFormData = new FormData();
        selectedImages.forEach(image => {
          imageFormData.append('files', image);
        });

        const uploadResponse = await axios.post('/api/upload/images', imageFormData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        if (uploadResponse.data.success && uploadResponse.data.files) {
          imageUrls = uploadResponse.data.files.map(file => file.url);
        }
      }

      // Create post with image URLs
      const postData = {
        boardId: parseInt(boardId),
        userId: user.id,
        title: formData.title.trim(),
        content: formData.content.trim(),
        imageUrls: imageUrls, // Include uploaded image URLs
        viewCount: 0,
        likeCount: 0,
        dislikeCount: 0,
        commentCount: 0
      };

      const response = await axios.post('/api/posts', postData);

      // Clean up preview URLs
      imagePreviewUrls.forEach(url => URL.revokeObjectURL(url));

      // Success - redirect to post detail
      navigate(`/boards/${boardId}/posts/${response.data.id}`, {
        state: { board }
      });
    } catch (err) {
      console.error('ÊäïÁ®ø„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      setError(err.response?.data?.error || 'ÊäïÁ®ø„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ„ÇÇ„ÅÜ‰∏ÄÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (window.confirm('‰ΩúÊàê‰∏≠„ÅÆÂÜÖÂÆπ„ÅåÂ§±„Çè„Çå„Åæ„Åô„Åå„ÄÅ„Çà„Çç„Åó„ÅÑ„Åß„Åô„Åã?')) {
      // Clean up preview URLs
      imagePreviewUrls.forEach(url => URL.revokeObjectURL(url));
      navigate(`/boards/${boardId}`, { state: { board } });
    }
  };

  return (
    <div className="post-write-page">
      <div className="post-write-container">
        <div className="write-header">
          <h2>Êñ∞Ë¶èÊäïÁ®ø‰ΩúÊàê</h2>
          <div className="board-info">
            <span className="board-name">{board?.name || 'Êé≤Á§∫Êùø'}</span>
            {board?.isAnonymous && <span className="anonymous-badge">ÂåøÂêç</span>}
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="write-form">
          <div className="form-group">
            <label htmlFor="title">„Çø„Ç§„Éà„É´</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
              maxLength={255}
              disabled={loading}
            />
            <div className="char-count">{formData.title.length} / 255</div>
          </div>

          <div className="form-group">
            <label htmlFor="content">ÂÜÖÂÆπ</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="ÂÜÖÂÆπ„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
              maxLength={5000}
              rows={15}
              disabled={loading}
            />
            <div className="char-count">{formData.content.length} / 5000</div>
          </div>

          {/* Image upload section */}
          <div className="form-group">
            <label>
              ÁîªÂÉè ({selectedImages.length} / 5Êûö„ÄÅÂêÑ5MB‰ª•‰∏ã)
            </label>
            <div className="image-upload-section">
              <input
                type="file"
                id="image-upload"
                accept="image/jpeg,image/jpg,image/png,image/gif,image/webp"
                multiple
                onChange={handleImageSelect}
                disabled={loading || selectedImages.length >= 5}
                style={{ display: 'none' }}
              />
              <label htmlFor="image-upload" className="upload-button">
                üì∑ ÁîªÂÉè„ÇíÈÅ∏Êäû
              </label>

              {/* Image previews */}
              {imagePreviewUrls.length > 0 && (
                <div className="image-preview-container">
                  {imagePreviewUrls.map((url, index) => (
                    <div key={index} className="image-preview-item">
                      <img src={url} alt={`Preview ${index + 1}`} />
                      <button
                        type="button"
                        className="remove-image-button"
                        onClick={() => handleRemoveImage(index)}
                        disabled={loading}
                      >
                        ‚úï
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          <div className="button-group">
            <button
              type="button"
              className="cancel-button"
              onClick={handleCancel}
              disabled={loading}
            >
              „Ç≠„É£„É≥„Çª„É´
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={loading}
            >
              {loading ? 'ÊäïÁ®ø‰∏≠...' : 'ÊäïÁ®ø„Åô„Çã'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default PostWritePage;
