              üëé „Çà„Åè„Å™„ÅÑ ({post.dislikeCount || 0})
            </button>
            <button className="action-button scrap-button">
              ‚≠ê „Çπ„ÇØ„É©„ÉÉ„Éó
            </button>
            <button className="action-button share-button">
              üîó ÂÖ±Êúâ
            </button>
          </div>
        </div>

        <CommentSection
          postId={postId}
          boardId={boardId}
          isAnonymous={board?.isAnonymous || false}
        />
      </div>
    </div>
  );
}

export default PostDetailPage;
