import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './PostWritePage.css';

function PostWritePage() {
  const { boardId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const board = location.state?.board;

  const [formData, setFormData] = useState({
    title: '',
    content: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // TODO(human): Add image upload state management here
  // Hint: You need state for selected files and preview URLs
  const [selectedImages, setSelectedImages] = useState([]);
  const [imagePreviewUrls, setImagePreviewUrls] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  // TODO(human): Implement handleImageSelect function
  // This function should:
  // 1. Validate file count (max 5) and file size (max 5MB each)
  // 2. Create preview URLs using FileReader
  // 3. Update selectedImages and imagePreviewUrls state

  // TODO(human): Implement handleRemoveImage function
  // This function should remove an image from both selectedImages and imagePreviewUrls

  const handleSubmit = async (e) => {

    const files = Array.from(e.target.files);
    if(selectedImages.length + files.length > 5) {
       setError("");
       return;
    }

    const validFiles = [];
    const validPreviews = [];

    for(const file of files) {
        if(file.size > 5 * 1024 * 1024) {
        setError("")
        continue;
        }
    }

    const validTypes = ['image/jpeg', 'image/jpg', 'image/png'];
    if (!validTypes.include(file))

    e.preventDefault();

    // Validation
    if (!formData.title.trim()) {
      setError('タイトルを入力してください');
      return;
    }
    if (!formData.content.trim()) {
      setError('内容を入力してください');
      return;
    }

    // Get current user
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!user.id) {
      setError('ログインが必要です');
      navigate('/login');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // TODO(human): Implement image upload logic here
      // 1. If there are selected images, upload them first to /api/upload/images
      // 2. Get the image URLs from the response
      // 3. Include the image URLs in the post data

      const postData = {
        boardId: parseInt(boardId),
        userId: user.id,
        title: formData.title.trim(),
        content: formData.content.trim(),
        viewCount: 0,
        likeCount: 0,
        dislikeCount: 0,
        commentCount: 0
        // TODO(human): Add imageUrls field here after uploading images
      };

      const response = await axios.post('/api/posts', postData);

      // Success - redirect to post detail
      navigate(`/boards/${boardId}/posts/${response.data.id}`, {
        state: { board }
      });
    } catch (err) {
      console.error('投稿の作成に失敗しました:', err);
      setError('投稿の作成に失敗しました。もう一度お試しください。');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (window.confirm('作成中の内容が失われますが、よろしいですか?')) {
      navigate(`/boards/${boardId}`, { state: { board } });
    }
  };

  return (
    <div className="post-write-page">
      <div className="post-write-container">
        <div className="write-header">
          <h2>新規投稿作成</h2>
          <div className="board-info">
            <span className="board-name">{board?.name || '掲示板'}</span>
            {board?.isAnonymous && <span className="anonymous-badge">匿名</span>}
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="write-form">
          <div className="form-group">
            <label htmlFor="title">タイトル</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="タイトルを入力してください"
              maxLength={255}
              disabled={loading}
            />
            <div className="char-count">{formData.title.length} / 255</div>
          </div>

          <div className="form-group">
            <label htmlFor="content">内容</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="内容を入力してください"
              maxLength={5000}
              rows={15}
              disabled={loading}
            />
            <div className="char-count">{formData.content.length} / 5000</div>
          </div>

          {/* TODO(human): Add image upload UI here */}
          {/* Include:
              - File input button
              - Image preview section with thumbnails
              - Remove button for each image
              - Display file count (e.g., "2 / 5 images")
          */}
          <div className="form-group">
            <label>画像 (最大5枚、各5MB以下)</label>
            {/* TODO(human): Add file input and preview UI here */}
          </div>

          <div className="button-group">
            <button
              type="button"
              className="cancel-button"
              onClick={handleCancel}
              disabled={loading}
            >
              キャンセル
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={loading}
            >
              {loading ? '投稿中...' : '投稿する'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default PostWritePage;
