import React, { useState, useEffect } from 'react';
import { TimetableCourse, COURSE_COLORS, DAYS } from '../../types/timetable.ts';
import './CourseModal.css';

interface CourseModalProps {
  course: TimetableCourse | null;
  defaultDay?: number;
  defaultPeriod?: number;
  onSave: (course: TimetableCourse) => void;
  onDelete?: () => void;
  onClose: () => void;
}

const CourseModal: React.FC<CourseModalProps> = ({
  course,
  defaultDay,
  defaultPeriod,
  onSave,
  onDelete,
  onClose
}) => {
  const [formData, setFormData] = useState<TimetableCourse>({
    courseName: '',
    professorName: '',
    classroom: '',
    daysOfWeek: defaultDay ? [defaultDay] : [1],
    periodStart: defaultPeriod || 1,
    periodEnd: defaultPeriod || 1,
    credits: 3,
    colorCode: COURSE_COLORS[0],
    memo: ''
  });

  useEffect(() => {
    if (course) {
      // 하위 호환성: dayOfWeek가 있으면 daysOfWeek로 변환
      const updatedCourse = {
        ...course,
        daysOfWeek: course.daysOfWeek || (course.dayOfWeek ? [course.dayOfWeek] : [1])
      };
      setFormData(updatedCourse);
    }
  }, [course]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.courseName.trim()) {
      alert('과목명을 입력해주세요');
      return;
    }

    if (formData.periodStart > formData.periodEnd) {
      alert('시작 교시는 종료 교시보다 작아야 합니다');
      return;
    }

    onSave(formData);
  };

  const handleChange = (field: keyof TimetableCourse, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const toggleDay = (dayNumber: number) => {
    setFormData(prev => {
      const currentDays = prev.daysOfWeek || [];
      const newDays = currentDays.includes(dayNumber)
        ? currentDays.filter(d => d !== dayNumber)
        : [...currentDays, dayNumber].sort();
      return { ...prev, daysOfWeek: newDays };
    });
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>{course ? '과목 수정' : '과목 추가'}</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>과목명 *</label>
            <input
              type="text"
              value={formData.courseName}
              onChange={(e) => handleChange('courseName', e.target.value)}
              placeholder="예: 자료구조와 알고리즘"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>교수님</label>
              <input
                type="text"
                value={formData.professorName || ''}
                onChange={(e) => handleChange('professorName', e.target.value)}
                placeholder="예: 홍길동"
              />
            </div>

            <div className="form-group">
              <label>강의실</label>
              <input
                type="text"
                value={formData.classroom || ''}
                onChange={(e) => handleChange('classroom', e.target.value)}
                placeholder="예: A101"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>요일 *</label>
              <select
                value={formData.dayOfWeek}
                onChange={(e) => handleChange('dayOfWeek', Number(e.target.value))}
              >
                <option value={1}>월요일</option>
                <option value={2}>화요일</option>
                <option value={3}>수요일</option>
                <option value={4}>목요일</option>
                <option value={5}>금요일</option>
              </select>
            </div>

            <div className="form-group">
              <label>시작 교시 *</label>
              <select
                value={formData.periodStart}
                onChange={(e) => handleChange('periodStart', Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5, 6, 7].map(p => (
                  <option key={p} value={p}>{p}교시</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>종료 교시 *</label>
              <select
                value={formData.periodEnd}
                onChange={(e) => handleChange('periodEnd', Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5, 6, 7].map(p => (
                  <option key={p} value={p}>{p}교시</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>학점</label>
              <input
                type="number"
                step="0.5"
                min="0"
                max="10"
                value={formData.credits || ''}
                onChange={(e) => handleChange('credits', parseFloat(e.target.value))}
              />
            </div>
          </div>

          <div className="form-group">
            <label>색상</label>
            <div className="color-picker">
              {COURSE_COLORS.map(color => (
                <button
                  key={color}
                  type="button"
                  className={`color-option ${formData.colorCode === color ? 'selected' : ''}`}
                  style={{ backgroundColor: color }}
                  onClick={() => handleChange('colorCode', color)}
                />
              ))}
            </div>
          </div>

          <div className="form-group">
            <label>메모</label>
            <textarea
              value={formData.memo || ''}
              onChange={(e) => handleChange('memo', e.target.value)}
              placeholder="예: 중간고사 있음"
              rows={3}
            />
          </div>

          <div className="modal-actions">
            {onDelete && (
              <button type="button" className="delete-button" onClick={onDelete}>
                삭제
              </button>
            )}
            <button type="button" className="cancel-button" onClick={onClose}>
              취소
            </button>
            <button type="submit" className="save-button">
              저장
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CourseModal;
