import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import CommentSection from './CommentSection';
import './PostDetailPage.css';

function PostDetailPage() {
  const { boardId, postId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const board = location.state?.board;

  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPost();
    incrementViewCount();
  }, [postId]);

  const fetchPost = async () => {
    try {
      const response = await axios.get(`/api/posts/${postId}`);
      setPost(response.data);
      setLoading(false);
    } catch (error) {
      console.error('ÊäïÁ®ø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      // „É¢„ÉÉ„ÇØ„Éá„Éº„Çø
      const mockPost = {
        id: postId,
        title: 'ÊôÇÈñìÂâ≤ÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†„ÅÆ‰Ωø„ÅÑÊñπ„Å´„Å§„ÅÑ„Å¶',
        content: `# ÊôÇÈñìÂâ≤ÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†„ÅÆ‰Ωø„ÅÑÊñπ

„Åì„ÅÆ„Ç∑„Çπ„ÉÜ„É†„Çí‰Ωø„Å£„Å¶Á∞°Âçò„Å´ÊôÇÈñìÂâ≤„Çí‰ΩúÊàê„ÉªÁÆ°ÁêÜ„Åß„Åç„Åæ„Åô„ÄÇ

## ‰∏ª„Å™Ê©üËÉΩ
1. ÊôÇÈñìÂâ≤„ÅÆ‰ΩúÊàê
2. ÁßëÁõÆ„ÅÆËøΩÂä†„ÉªÂâäÈô§
3. ÊôÇÈñìÂâ≤„ÅÆÂÖ±Êúâ
4. „Ç®„ÇØ„Çπ„Éù„Éº„ÉàÊ©üËÉΩ

„Åú„Å≤Ê¥ªÁî®„Åó„Å¶„Åø„Å¶„Åè„Å†„Åï„ÅÑÔºÅ`,
        userId: 1,
        viewCount: 234,
        likeCount: 12,
        commentCount: 5,
        createdAt: '2026-01-20T10:30:00',
        updatedAt: '2026-01-20T10:30:00'
      };
      setPost(mockPost);
      setLoading(false);
    }
  };

  const incrementViewCount = async () => {
    try {
      // Get current user if logged in
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const userId = user.id || null;

      // Send user ID as query parameter
      const params = userId ? { userId } : {};
      await axios.post(`/api/posts/${postId}/view`, null, { params });
    } catch (error) {
      console.error('Èñ≤Ë¶ßÊï∞„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
    }
  };

  const handleLikePost = async () => {
    try {
      // Get current user if logged in
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const userId = user.id || null;

      // Send user ID as query parameter
      const params = userId ? { userId } : {};
      await axios.post(`/api/posts/${postId}/like`, null, { params });
      fetchPost();
    } catch (error) {
      console.error('„ÅÑ„ÅÑ„Å≠„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
    }
  };

  const handleBack = () => {
    navigate(`/boards/${boardId}/posts`, { state: { board } });
  };

  const handleEdit = () => {
    navigate(`/boards/${boardId}/posts/${postId}/edit`, {
      state: { board, post }
    });
  };

  const handleDelete = async () => {
    if (!window.confirm('Êú¨ÂΩì„Å´„Åì„ÅÆÊäïÁ®ø„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü')) {
      return;
    }

    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!user.id) {
      alert('„É≠„Ç∞„Ç§„É≥„ÅåÂøÖË¶Å„Åß„Åô');
      return;
    }

    try {
      await axios.delete(`/api/posts/${postId}?userId=${user.id}`);
      alert('ÊäïÁ®ø„ÅåÂâäÈô§„Åï„Çå„Åæ„Åó„Åü');
      navigate(`/boards/${boardId}/posts`, { state: { board } });
    } catch (error) {
      console.error('ÊäïÁ®ø„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      if (error.response && error.response.status === 403) {
        alert('Ëá™ÂàÜ„ÅÆÊäïÁ®ø„ÅÆ„ÅøÂâäÈô§„Åß„Åç„Åæ„Åô„ÄÇ');
      } else {
        alert('ÊäïÁ®ø„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }
    }
  };

  const canModifyPost = () => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return user.id && post && user.id === post.userId;
  };

  const getFormattedDate = (dateString) => {
    if (!dateString) return '---';

    const date = new Date(dateString);

    // Check if date is valid
    if (isNaN(date.getTime())) return '---';

    return date.toLocaleString('ja-JP', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>;
  }

  if (!post) {
    return <div className="error">ÊäïÁ®ø„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü</div>;
  }

  return (
    <div className="post-detail-page">
      <div className="post-detail-container">
        <div className="post-detail-header">
          <button className="back-button" onClick={handleBack}>
            ‚Üê ‰∏ÄË¶ß„Å´Êàª„Çã
          </button>
          <div className="header-right">
            <div className="board-badge">{board?.name || 'Êé≤Á§∫Êùø'}</div>
            {canModifyPost() && (
              <>
                <button className="edit-button" onClick={handleEdit}>
                  ‚úèÔ∏è Á∑®ÈõÜ
                </button>
                <button className="delete-button" onClick={handleDelete}>
                  üóëÔ∏è ÂâäÈô§
                </button>
              </>
            )}
          </div>
        </div>

        <div className="post-content-wrapper">
          <div className="post-header">
            <h1 className="post-title">{post.title}</h1>
            <div className="post-meta">
              <div className="meta-left">
                <span className="author">
                  {board?.isAnonymous ? post.anonymousId || 'ÂåøÂêç' : (post.authorNickname || 'Unknown User')}
                </span>
                <span className="separator">‚Ä¢</span>
                <span className="date">{getFormattedDate(post.createdAt)}</span>
                {post.updatedAt && post.createdAt &&
                 new Date(post.updatedAt).getTime() - new Date(post.createdAt).getTime() > 1000 && (
                  <>
                    <span className="separator">‚Ä¢</span>
                    <span className="edited">(Á∑®ÈõÜÊ∏à„Åø)</span>
                  </>
