import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './PostListPage.css';

function PostListPage() {
  const { boardId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const board = location.state?.board;

  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [sortBy, setSortBy] = useState('recent');
  const [searchKeyword, setSearchKeyword] = useState('');

  useEffect(() => {
    fetchPosts();
  }, [boardId, currentPage, sortBy]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`/api/posts/board/${boardId}`);
      // „É¨„Çπ„Éù„É≥„Çπ„ÅØList<Post>„ÇíÁõ¥Êé•Ëøî„Åô„ÅÆ„Åß„ÄÅ.content„Åß„ÅØ„Å™„Åè.data„Çí‰ΩøÁî®
      const posts = Array.isArray(response.data) ? response.data : [];
      setPosts(posts);
      setTotalPages(Math.ceil(posts.length / 20)); // Á∞°ÊòìÁöÑ„Å™„Éö„Éº„Ç∏„Éç„Éº„Ç∑„Éß„É≥
      setLoading(false);
    } catch (error) {
      console.error('ÊäïÁ®ø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      // „É¢„ÉÉ„ÇØ„Éá„Éº„Çø
      const mockPosts = [
        { id: 1, title: 'ÊôÇÈñìÂâ≤ÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†„ÅÆ‰Ωø„ÅÑÊñπ', userId: 1, viewCount: 234, likeCount: 12, commentCount: 5, createdAt: '2026-01-20T10:30:00' },
        { id: 2, title: '„Åä„Åô„Åô„ÇÅ„ÅÆÊïôÈ§äÁßëÁõÆÊïô„Åà„Å¶„Åè„Å†„Åï„ÅÑ', userId: 2, viewCount: 156, likeCount: 8, commentCount: 15, createdAt: '2026-01-20T09:15:00' },
        { id: 3, title: 'Âõ≥Êõ∏È§®„ÅÆÂ∫ßÂ∏≠‰∫àÁ¥Ñ„ÅÆ„Ç≥„ÉÑ', userId: 3, viewCount: 89, likeCount: 6, commentCount: 3, createdAt: '2026-01-19T16:45:00' },
      ];
      setPosts(mockPosts);
      setTotalPages(1);
      setLoading(false);
    }
  };

  const handleSortChange = (newSortBy) => {
    setSortBy(newSortBy);
    setCurrentPage(0);
  };

  const handleSearch = async () => {
    if (!searchKeyword.trim()) {
      fetchPosts();
      return;
    }

    try {
      const response = await axios.get(`/api/posts/search`, {
        params: {
          keyword: searchKeyword
        }
      });
      const searchResults = Array.isArray(response.data) ? response.data : [];
      // ÁèæÂú®„ÅÆÊé≤Á§∫Êùø„ÅÆÊäïÁ®ø„ÅÆ„Åø„Éï„Ç£„É´„Çø„É™„É≥„Ç∞
      const filteredPosts = searchResults.filter(post => post.boardId === parseInt(boardId));
      setPosts(filteredPosts);
      setTotalPages(Math.ceil(filteredPosts.length / 20));
      setCurrentPage(0);
    } catch (error) {
      console.error('Ê§úÁ¥¢„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      // „Ç®„É©„ÉºÊôÇ„ÅØÂÖ®ÊäïÁ®ø„ÇíË°®Á§∫
      fetchPosts();
    }
  };

  const handlePostClick = (postId) => {
    navigate(`/boards/${boardId}/posts/${postId}`, { state: { board } });
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const past = new Date(dateString);
    const diffInMinutes = Math.floor((now - past) / (1000 * 60));

    if (diffInMinutes < 60) return `${diffInMinutes}ÂàÜÂâç`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}ÊôÇÈñìÂâç`;
    return `${Math.floor(diffInMinutes / 1440)}Êó•Ââç`;
  };

  if (loading) {
    return <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>;
  }

  return (
    <div className="post-list-page">
      <div className="post-list-container">
        <div className="post-list-header">
          <div className="board-title-section">
            <button className="back-button" onClick={() => navigate('/boards')}>
              ‚Üê Êé≤Á§∫Êùø‰∏ÄË¶ß
            </button>
            <h2>{board?.name || 'Êé≤Á§∫Êùø'}</h2>
            {board?.isAnonymous && <span className="anonymous-badge">ÂåøÂêç</span>}
          </div>

          <div className="controls-section">
            <div className="search-box">
              <input
                type="text"
                placeholder="Ê§úÁ¥¢..."
                value={searchKeyword}
                onChange={(e) => setSearchKeyword(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button onClick={handleSearch}>üîç</button>
            </div>

            <div className="sort-buttons">
              <button
                className={sortBy === 'recent' ? 'active' : ''}
                onClick={() => handleSortChange('recent')}
              >
                ÊúÄÊñ∞È†Ü
              </button>
              <button
                className={sortBy === 'likes' ? 'active' : ''}
                onClick={() => handleSortChange('likes')}
              >
                ‰∫∫Ê∞óÈ†Ü
              </button>
              <button
                className={sortBy === 'views' ? 'active' : ''}
                onClick={() => handleSortChange('views')}
              >
                Èñ≤Ë¶ßÈ†Ü
              </button>
            </div>

            <button
              className="write-post-button"
              onClick={() => {
                // TODO(human): Add console.log statements here to debug navigation
                navigate(`/boards/${boardId}/write`, { state: { board } });
              }}
            >
              ÊäïÁ®ø„Åô„Çã
            </button>
          </div>
        </div>

        <div className="posts-table">
          <div className="table-header">
            <div className="col-title">„Çø„Ç§„Éà„É´</div>
            <div className="col-author">‰ΩúÊàêËÄÖ</div>
            <div className="col-time">‰ΩúÊàêÊó•ÊôÇ</div>
            <div className="col-stats">Áµ±Ë®à</div>
          </div>

          {posts.map(post => (
            <div
              key={post.id}
              className="post-row"
              onClick={() => handlePostClick(post.id)}
            >
              <div className="col-title">
                <span className="post-title">{post.title}</span>
                {post.commentCount > 0 && (
                  <span className="comment-count">[{post.commentCount}]</span>
                )}
              </div>
              <div className="col-author">
                {board?.isAnonymous ? post.anonymousId || 'ÂåøÂêç' : `„É¶„Éº„Ç∂„Éº${post.userId}`}
              </div>
              <div className="col-time">{getTimeAgo(post.createdAt)}</div>
              <div className="col-stats">
                <span>üëÅ {post.viewCount}</span>
                <span>üëç {post.likeCount}</span>
              </div>
            </div>
          ))}
        </div>

        {totalPages > 1 && (
          <div className="pagination">
            <button
              disabled={currentPage === 0}
              onClick={() => setCurrentPage(currentPage - 1)}
            >
              Ââç„Å∏
            </button>
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index}
                className={currentPage === index ? 'active' : ''}
                onClick={() => setCurrentPage(index)}
              >
                {index + 1}
              </button>
            ))}
            <button
              disabled={currentPage === totalPages - 1}
              onClick={() => setCurrentPage(currentPage + 1)}
            >
              Ê¨°„Å∏
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default PostListPage;
