import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TimetableCourse, Timetable, DAYS, PERIODS } from '../../types/timetable.ts';
import CourseModal from './CourseModal.tsx';
import './TimetablePage.css';

const TimetablePage: React.FC = () => {
  const [timetable, setTimetable] = useState<Timetable | null>(null);
  const [courses, setCourses] = useState<TimetableCourse[]>([]);
  const [selectedSemester, setSelectedSemester] = useState('spring');
  const [selectedYear, setSelectedYear] = useState(2026);
  const [selectedUserId, setSelectedUserId] = useState(1);
  const [users, setUsers] = useState<any[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState<{day: number, period: number} | null>(null);
  const [editingCourse, setEditingCourse] = useState<TimetableCourse | null>(null);

  useEffect(() => {
    loadUsers();
  }, []);

  useEffect(() => {
    loadTimetable();
  }, [selectedSemester, selectedYear, selectedUserId]);

  const loadUsers = async () => {
    try {
      const response = await axios.get('/api/users');
      setUsers(response.data);
      if (response.data.length > 0) {
        setSelectedUserId(response.data[0].id);
      }
    } catch (error) {
      console.error('ÏÇ¨Ïö©Ïûê Î™©Î°ù Î°úÎî© Ïã§Ìå®', error);
    }
  };

  const loadTimetable = async () => {
    try {
      const response = await axios.get('/api/timetable', {
        params: {
          semester: selectedSemester,
          year: selectedYear,
          userId: selectedUserId
        }
      });
      setTimetable(response.data.timetable);
      setCourses(response.data.courses);
    } catch (error) {
      console.error('ÏãúÍ∞ÑÌëú Î°úÎî© Ïã§Ìå®', error);
    }
  };

  const handleSlotClick = (day: number, period: number) => {
    const existingCourse = courses.find(
      c => c.dayOfWeek === day &&
           c.periodStart <= period &&
           c.periodEnd >= period
    );

    if (existingCourse) {
      setEditingCourse(existingCourse);
    } else {
      setSelectedSlot({ day, period });
    }
    setIsModalOpen(true);
  };

  const handleSaveCourse = async (course: TimetableCourse) => {
    try {
      if (!timetable || !timetable.timetableId) {
        alert('ÏãúÍ∞ÑÌëúÎ•º Î®ºÏ†Ä Î∂àÎü¨ÏôÄÏ£ºÏÑ∏Ïöî');
        return;
      }

      if (course.courseId) {
        await axios.put(`/api/timetable/course/${course.courseId}`, course);
      } else {
        await axios.post('/api/timetable/course', {
          ...course,
          timetableId: timetable.timetableId
        });
      }
      loadTimetable();
      closeModal();
    } catch (error: any) {
      alert(error.response?.data || 'Ï†ÄÏû• Ïã§Ìå®');
    }
  };

  const handleDeleteCourse = async (courseId: number) => {
    if (window.confirm('Ïù¥ Í≥ºÎ™©ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        await axios.delete(`/api/timetable/course/${courseId}`);
        loadTimetable();
        closeModal();
      } catch (error) {
        alert('ÏÇ≠Ï†ú Ïã§Ìå®');
      }
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedSlot(null);
    setEditingCourse(null);
  };

  const getCourseAtSlot = (day: number, period: number): TimetableCourse | undefined => {
    return courses.find(
      c => c.dayOfWeek === day &&
           c.periodStart <= period &&
           c.periodEnd >= period
    );
  };

  const totalCredits = courses.reduce((sum, course) => {
    return sum + (course.credits || 0);
  }, 0);

  return (
    <div className="timetable-container">
      <div className="credits-summary">
        üìö Ï¥ù ÌïôÏ†ê: <strong>{totalCredits.toFixed(1)}</strong>
      </div>

      <div className="timetable-header">
        <h1>ÏãúÍ∞ÑÌëú</h1>
      </div>

      <div className="semester-selector">
        <select
          value={selectedUserId}
          onChange={(e) => setSelectedUserId(Number(e.target.value))}
          className="user-selector"
        >
          {users.length === 0 ? (
            <option value={1}>ÏÇ¨Ïö©ÏûêÎ•º Ï∂îÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî</option>
          ) : (
            users.map(user => (
              <option key={user.id} value={user.id}>
                {user.name} ({user.email})
              </option>
            ))
          )}
        </select>
        <select
          value={selectedYear}
          onChange={(e) => setSelectedYear(Number(e.target.value))}
        >
          <option value={2024}>2024ÎÖÑ</option>
          <option value={2025}>2025ÎÖÑ</option>
          <option value={2026}>2026ÎÖÑ</option>
        </select>
        <select
          value={selectedSemester}
          onChange={(e) => setSelectedSemester(e.target.value)}
        >
          <option value="spring">Î¥ÑÌïôÍ∏∞</option>
          <option value="fall">Í∞ÄÏùÑÌïôÍ∏∞</option>
        </select>
      </div>

      <div className="timetable-grid">
        <div className="grid-header">
          <div className="period-column"></div>
          {DAYS.map(day => (
            <div key={day} className="day-header">{day}</div>
          ))}
        </div>

        {PERIODS.map(period => (
          <div key={period.number} className="grid-row">
            <div className="period-cell">
              <div className="period-number">{period.number}Èôê</div>
              <div className="period-time">{period.time}</div>
            </div>
            {DAYS.map((_, dayIndex) => {
              const course = getCourseAtSlot(dayIndex + 1, period.number);
              const isStart = course && course.periodStart === period.number;
              const span = course ? (course.periodEnd - course.periodStart + 1) : 1;

              if (course && !isStart) {
                return null; // ÈÄ£Á∂ö„Åô„ÇãÁßëÁõÆ„ÅØÊúÄÂàù„ÅÆ„Çª„É´„Å†„ÅëË°®Á§∫
              }

              return (
                <div
                  key={`${dayIndex}-${period.number}`}
                  className={`course-cell ${course ? 'has-course' : ''}`}
                  style={{
                    backgroundColor: course?.colorCode || 'transparent',
                    gridRow: isStart ? `span ${span}` : undefined
                  }}
                  onClick={() => handleSlotClick(dayIndex + 1, period.number)}
                >
                  {course && (
                    <div className="course-info">
                      <div className="course-name">{course.courseName}</div>
                      {course.classroom && (
                        <div className="course-classroom">{course.classroom}</div>
                      )}
                      {course.professorName && (
                        <div className="course-professor">{course.professorName}</div>
                      )}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      {isModalOpen && (
        <CourseModal
          course={editingCourse}
          defaultDay={selectedSlot?.day}
          defaultPeriod={selectedSlot?.period}
          onSave={handleSaveCourse}
          onDelete={editingCourse ? () => handleDeleteCourse(editingCourse.courseId!) : undefined}
          onClose={closeModal}
        />
      )}
    </div>
  );
};

export default TimetablePage;
