    return icons[code] || 'üìã';
  };

  if (loading) {
    return <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>;
  }

  return (
    <div className="board-list-page">
      <div className="board-list-container">
        <div className="board-list-header">
          <h1>Êé≤Á§∫Êùø‰∏ÄË¶ß</h1>
