import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './MainPage.css';

function MainPage() {
  const navigate = useNavigate();
  const [boards, setBoards] = useState([]);
  const [boardPosts, setBoardPosts] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBoardsAndPosts();
  }, []);

  const fetchBoardsAndPosts = async () => {
    try {
      // 掲示板一覧を取得
      const boardsResponse = await axios.get('/ej2/api/boards');
      const boardsData = boardsResponse.data;
      setBoards(boardsData);

      // 各掲示板の投稿を5件ずつ取得
      const postsData = {};
      for (const board of boardsData) {
        try {
          const postsResponse = await axios.get(`/ej2/api/posts/board/${board.id}`, {
            params: { page: 0, size: 5, sort: 'recent' }
          });
          postsData[board.id] = postsResponse.data.content;
        } catch (error) {
          console.error(`掲示板${board.id}の投稿取得に失敗:`, error);
          postsData[board.id] = [];
        }
      }
      setBoardPosts(postsData);
      setLoading(false);
    } catch (error) {
      console.error('データの読み込みに失敗:', error);
      // モックデータ
      const mockBoards = [
        { id: 1, code: 'GENERAL', name: '自由掲示板', isAnonymous: false },
        { id: 2, code: 'ANONYMOUS', name: '匿名掲示板', isAnonymous: true },
        { id: 3, code: 'EVENT', name: 'イベント掲示板', isAnonymous: false },
        { id: 4, code: 'MARKET', name: '中古市場', isAnonymous: false },
      ];

      const mockPosts = {
        1: [
          { id: 1, title: '時間割管理システムの使い方', userId: 1, viewCount: 234, likeCount: 12, commentCount: 5, createdAt: '2026-01-20T10:30:00' },
          { id: 2, title: 'おすすめの教養科目教えてください', userId: 2, viewCount: 156, likeCount: 8, commentCount: 15, createdAt: '2026-01-20T09:15:00' },
          { id: 3, title: '図書館の座席予約のコツ', userId: 3, viewCount: 89, likeCount: 6, commentCount: 3, createdAt: '2026-01-19T16:45:00' },
          { id: 4, title: '履修登録期間について', userId: 1, viewCount: 120, likeCount: 10, commentCount: 7, createdAt: '2026-01-19T14:20:00' },
          { id: 5, title: 'サークル新歓情報', userId: 4, viewCount: 98, likeCount: 15, commentCount: 4, createdAt: '2026-01-19T11:30:00' },
        ],
        2: [
          { id: 11, title: '授業で寝てる人多すぎ', userId: 5, anonymousId: '匿名1', viewCount: 456, likeCount: 34, commentCount: 23, createdAt: '2026-01-20T11:00:00' },
          { id: 12, title: '教授の評判ってどう？', userId: 6, anonymousId: '匿名2', viewCount: 234, likeCount: 18, commentCount: 12, createdAt: '2026-01-20T08:30:00' },
          { id: 13, title: 'この大学選んでよかった', userId: 7, anonymousId: '匿名3', viewCount: 567, likeCount: 45, commentCount: 34, createdAt: '2026-01-19T18:20:00' },
          { id: 14, title: '課題が多すぎる件', userId: 8, anonymousId: '匿名4', viewCount: 345, likeCount: 28, commentCount: 19, createdAt: '2026-01-19T15:10:00' },
          { id: 15, title: '食堂のおすすめメニュー', userId: 9, anonymousId: '匿名5', viewCount: 189, likeCount: 22, commentCount: 11, createdAt: '2026-01-19T12:45:00' },
        ],
        3: [
          { id: 21, title: '学園祭ボランティア募集', userId: 10, viewCount: 123, likeCount: 9, commentCount: 6, createdAt: '2026-01-20T09:00:00' },
          { id: 22, title: '就活セミナー開催のお知らせ', userId: 11, viewCount: 234, likeCount: 15, commentCount: 8, createdAt: '2026-01-19T17:30:00' },
          { id: 23, title: 'スポーツ大会参加者募集', userId: 12, viewCount: 156, likeCount: 12, commentCount: 5, createdAt: '2026-01-19T14:00:00' },
          { id: 24, title: '交換留学説明会', userId: 13, viewCount: 198, likeCount: 20, commentCount: 9, createdAt: '2026-01-19T10:15:00' },
          { id: 25, title: '図書館イベント情報', userId: 14, viewCount: 87, likeCount: 7, commentCount: 2, createdAt: '2026-01-18T16:40:00' },
        ],
        4: [
          { id: 31, title: '教科書売ります（経済学入門）', userId: 15, viewCount: 145, likeCount: 8, commentCount: 4, createdAt: '2026-01-20T12:00:00' },
          { id: 32, title: 'ノートPC譲ります', userId: 16, viewCount: 289, likeCount: 23, commentCount: 15, createdAt: '2026-01-20T07:30:00' },
          { id: 33, title: '自転車探してます', userId: 17, viewCount: 134, likeCount: 6, commentCount: 3, createdAt: '2026-01-19T19:20:00' },
          { id: 34, title: '電子辞書買取希望', userId: 18, viewCount: 98, likeCount: 5, commentCount: 2, createdAt: '2026-01-19T13:50:00' },
          { id: 35, title: '家具無料で差し上げます', userId: 19, viewCount: 234, likeCount: 18, commentCount: 12, createdAt: '2026-01-19T09:30:00' },
        ],
      };

      setBoards(mockBoards);
      setBoardPosts(mockPosts);
      setLoading(false);
    }
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const past = new Date(dateString);
    const diffInMinutes = Math.floor((now - past) / (1000 * 60));

    if (diffInMinutes < 60) return `${diffInMinutes}分前`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}時間前`;
    return `${Math.floor(diffInMinutes / 1440)}日前`;
  };

  const handlePostClick = (boardId, postId, board) => {
    navigate(`/boards/${boardId}/posts/${postId}`, { state: { board } });
  };

  const handleBoardClick = (board) => {
    navigate(`/boards/${board.id}/posts`, { state: { board } });
  };

  if (loading) {
    return <div className="loading">読み込み中...</div>;
  }

  return (
    <div className="main-page">
      <div className="main-container-full">
        {boards.map(board => (
          <div key={board.id} className="board-section">
            <div className="board-section-header">
              <h2 className="board-section-title">
                {board.name}
                {board.isAnonymous && <span className="anonymous-badge">匿名</span>}
              </h2>
              <button
                className="view-more-button"
                onClick={() => handleBoardClick(board)}
              >
                もっと見る
              </button>
            </div>

            <div className="posts-list-horizontal">
              {boardPosts[board.id] && boardPosts[board.id].length > 0 ? (
                boardPosts[board.id].map(post => (
                  <div
                    key={post.id}
                    className="post-card"
                    onClick={() => handlePostClick(board.id, post.id, board)}
                  >
                    <div className="post-card-title">{post.title}</div>
                    <div className="post-card-meta">
                      <span className="post-card-author">
                        {board.isAnonymous ? post.anonymousId || '匿名' : `ユーザー${post.userId}`}
                      </span>
                      <span className="post-card-time">{getTimeAgo(post.createdAt)}</span>
                    </div>
                    <div className="post-card-stats">
                      <span>閲覧 {post.viewCount}</span>
                      <span>いいね {post.likeCount}</span>
                      <span>コメント {post.commentCount}</span>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-posts">まだ投稿がありません</div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MainPage;
