import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AuthPages.css';

/**
 * „É≠„Ç∞„Ç§„É≥„Éö„Éº„Ç∏„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
 */
function LoginPage() {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // „Éï„Ç©„Éº„É†ÂÖ•ÂäõÂ§âÊõ¥„Éè„É≥„Éâ„É©„Éº
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // „Ç®„É©„Éº„Çí„ÇØ„É™„Ç¢
  };

  // „É≠„Ç∞„Ç§„É≥„Éï„Ç©„Éº„É†ÈÄÅ‰ø°„Éè„É≥„Éâ„É©„Éº
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('/api/auth/login', {
        username: formData.username,
        password: formData.password
      }, { withCredentials: true });

      if (response.data.success) {
        // „É≠„Ç∞„Ç§„É≥ÊàêÂäü: „É¶„Éº„Ç∂„ÉºÊÉÖÂ†±„ÇílocalStorage„Å´‰øùÂ≠ò
        localStorage.setItem('user', JSON.stringify(response.data.user));

        // „É°„Ç§„É≥„Éö„Éº„Ç∏„Å∏„É™„ÉÄ„Ç§„É¨„ÇØ„Éà
        navigate('/');
      } else {
        setError(response.data.message);
      }
    } catch (err) {
      if (err.response && err.response.data) {
        setError(err.response.data.message || '„É≠„Ç∞„Ç§„É≥„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      } else {
        setError('„Çµ„Éº„Éê„Éº„Å®„ÅÆÈÄö‰ø°„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">Welcome Back üëä</h2>

        {error && (
          <div className="auth-error">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="username">„É¶„Éº„Ç∂„ÉºÂêç</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              placeholder="Username"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">„Éë„Çπ„ÉØ„Éº„Éâ</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Password"
              disabled={loading}
            />
          </div>

          <button
            type="submit"
            className="auth-button"
            disabled={loading}
          >
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <div className="auth-links">
          <a href="/register">Sign Up</a>
        </div>

        <div className="concept-links">
          view concept for <a href="/concept/mobile">mobile</a> or for <a href="/concept/desktop">desktop</a>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
