import React, { useState, useEffect } from 'react';
import { TimetableCourse, DaySchedule, COURSE_COLORS, DAYS } from '../../types/timetable.ts';
import './CourseModal.css';

interface CourseModalProps {
  course: TimetableCourse | null;
  defaultDay?: number;
  defaultPeriod?: number;
  onSave: (course: TimetableCourse) => void;
  onDelete?: () => void;
  onClose: () => void;
}

const CourseModal: React.FC<CourseModalProps> = ({
  course,
  defaultDay,
  defaultPeriod,
  onSave,
  onDelete,
  onClose
}) => {
  const [formData, setFormData] = useState<TimetableCourse>({
    courseName: '',
    professorName: '',
    classroom: '',
    daySchedules: defaultDay && defaultPeriod
      ? [{ day: defaultDay, periodStart: defaultPeriod, periodEnd: defaultPeriod }]
      : [{ day: 1, periodStart: 1, periodEnd: 1 }],
    credits: 3,
    colorCode: COURSE_COLORS[0],
    memo: ''
  });

  useEffect(() => {
    if (course) {
      // ÌïòÏúÑ Ìò∏ÌôòÏÑ±: Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞Î•º ÏÉàÎ°úÏö¥ Íµ¨Ï°∞Î°ú Î≥ÄÌôò
      let daySchedules: DaySchedule[] = [];

      if (course.daySchedules && course.daySchedules.length > 0) {
        daySchedules = course.daySchedules;
      } else if (course.daysOfWeek && course.daysOfWeek.length > 0) {
        // daysOfWeekÎ•º daySchedulesÎ°ú Î≥ÄÌôò
        daySchedules = course.daysOfWeek.map(day => ({
          day,
          periodStart: course.periodStart || 1,
          periodEnd: course.periodEnd || 1
        }));
      } else if (course.dayOfWeek) {
        daySchedules = [{
          day: course.dayOfWeek,
          periodStart: course.periodStart || 1,
          periodEnd: course.periodEnd || 1
        }];
      }

      setFormData({
        ...course,
        daySchedules
      });
    }
  }, [course]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.courseName.trim()) {
      alert('Í≥ºÎ™©Î™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî');
      return;
    }

    if (formData.periodStart > formData.periodEnd) {
      alert('ÏãúÏûë ÍµêÏãúÎäî Ï¢ÖÎ£å ÍµêÏãúÎ≥¥Îã§ ÏûëÏïÑÏïº Ìï©ÎãàÎã§');
      return;
    }

    // „Éá„Éê„ÉÉ„Ç∞: ÈÄÅ‰ø°„Éá„Éº„Çø„ÇíÁ¢∫Ë™ç
    console.log('üì§ ÈÄÅ‰ø°„Åô„Çã„Éá„Éº„Çø:', formData);
    console.log('üìÖ daysOfWeek:', formData.daysOfWeek);

    onSave(formData);
  };

  const handleChange = (field: keyof TimetableCourse, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const toggleDay = (dayNumber: number) => {
    setFormData(prev => {
      const currentDays = prev.daysOfWeek || [];
      const newDays = currentDays.includes(dayNumber)
        ? currentDays.filter(d => d !== dayNumber)
        : [...currentDays, dayNumber].sort();
      return { ...prev, daysOfWeek: newDays };
    });
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>{course ? 'Í≥ºÎ™© ÏàòÏ†ï' : 'Í≥ºÎ™© Ï∂îÍ∞Ä'}</h2>
          <button className="close-button" onClick={onClose}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Í≥ºÎ™©Î™Ö *</label>
            <input
              type="text"
              value={formData.courseName}
              onChange={(e) => handleChange('courseName', e.target.value)}
              placeholder="Ïòà: ÏûêÎ£åÍµ¨Ï°∞ÏôÄ ÏïåÍ≥†Î¶¨Ï¶ò"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>ÍµêÏàòÎãò</label>
              <input
                type="text"
                value={formData.professorName || ''}
                onChange={(e) => handleChange('professorName', e.target.value)}
                placeholder="Ïòà: ÌôçÍ∏∏Îèô"
              />
            </div>

            <div className="form-group">
              <label>Í∞ïÏùòÏã§</label>
              <input
                type="text"
                value={formData.classroom || ''}
                onChange={(e) => handleChange('classroom', e.target.value)}
                placeholder="Ïòà: A101"
              />
            </div>
          </div>

          <div className="form-group">
            <label>ÏöîÏùº * (Î≥µÏàò ÏÑ†ÌÉù Í∞ÄÎä•)</label>
            <div className="days-selector">
              {DAYS.map((day, index) => {
                const dayNumber = index + 1;
                const isSelected = formData.daysOfWeek?.includes(dayNumber);
                return (
                  <button
                    key={dayNumber}
                    type="button"
                    className={`day-button ${isSelected ? 'selected' : ''}`}
                    onClick={() => toggleDay(dayNumber)}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
          </div>

          <div className="form-row">

            <div className="form-group">
              <label>ÏãúÏûë ÍµêÏãú *</label>
              <select
                value={formData.periodStart}
                onChange={(e) => handleChange('periodStart', Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5, 6, 7].map(p => (
                  <option key={p} value={p}>{p}ÍµêÏãú</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Ï¢ÖÎ£å ÍµêÏãú *</label>
              <select
                value={formData.periodEnd}
                onChange={(e) => handleChange('periodEnd', Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5, 6, 7].map(p => (
                  <option key={p} value={p}>{p}ÍµêÏãú</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>ÌïôÏ†ê</label>
              <input
                type="number"
                step="0.5"
                min="0"
                max="10"
                value={formData.credits || ''}
                onChange={(e) => handleChange('credits', parseFloat(e.target.value))}
              />
            </div>
          </div>

          <div className="form-group">
            <label>ÏÉâÏÉÅ</label>
            <div className="color-picker">
              {COURSE_COLORS.map(color => (
                <button
                  key={color}
                  type="button"
                  className={`color-option ${formData.colorCode === color ? 'selected' : ''}`}
                  style={{ backgroundColor: color }}
                  onClick={() => handleChange('colorCode', color)}
                />
              ))}
            </div>
          </div>

          <div className="form-group">
            <label>Î©îÎ™®</label>
            <textarea
              value={formData.memo || ''}
              onChange={(e) => handleChange('memo', e.target.value)}
              placeholder="Ïòà: Ï§ëÍ∞ÑÍ≥†ÏÇ¨ ÏûàÏùå"
              rows={3}
            />
          </div>

          <div className="modal-actions">
            {onDelete && (
              <button type="button" className="delete-button" onClick={onDelete}>
                ÏÇ≠Ï†ú
              </button>
            )}
            <button type="button" className="cancel-button" onClick={onClose}>
              Ï∑®ÏÜå
            </button>
            <button type="submit" className="save-button">
              Ï†ÄÏû•
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CourseModal;
