import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import './ChatPage.css';

const GLOBAL_ROOM_ID = 1;

function ChatPage() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [nickname, setNickname] = useState('');
  const [connected, setConnected] = useState(false);
  const [connecting, setConnecting] = useState(true);
  const stompClientRef = useRef(null);
  const messagesEndRef = useRef(null);
  const subscriptionRef = useRef(null);
  const nicknameRef = useRef('');

  useEffect(() => {
    initChat();

    const handleBeforeUnload = () => {
      disconnectSync();
    };
    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
      disconnectSync();
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const initChat = async () => {
    try {
      // 1. Get nickname (anonymous)
      const nicknameRes = await axios.post(`/api/chat/rooms/${GLOBAL_ROOM_ID}/nickname`, {
        useAnonymous: true
      });
      const assignedNickname = nicknameRes.data.nickname;
      setNickname(assignedNickname);
      nicknameRef.current = assignedNickname;

      // 2. Ïù¥Ï†Ñ Î©îÏãúÏßÄ Î°úÎìúÌïòÏßÄ ÏïäÏùå (ÏÉàÎ°ú Îì§Ïñ¥Ïò® ÏÇ¨Ïö©ÏûêÎäî Ïù¥Ï†Ñ ÎåÄÌôî Î≥º Ïàò ÏóÜÏùå)
      // const messagesRes = await axios.get(`/api/chat/rooms/${GLOBAL_ROOM_ID}/messages`);
      // setMessages(messagesRes.data);
      setMessages([]);

      // 3. Connect WebSocket
      const client = new Client({
        webSocketFactory: () => new SockJS('/ws/chat'),
        debug: () => {},
        onConnect: () => {
          setConnected(true);
          setConnecting(false);
          stompClientRef.current = client;

          subscriptionRef.current = client.subscribe(
            `/topic/chat/${GLOBAL_ROOM_ID}`,
            (messageOutput) => {
              const msg = JSON.parse(messageOutput.body);
              setMessages(prev => [...prev, msg]);
            }
          );

          client.publish({
            destination: `/app/chat/${GLOBAL_ROOM_ID}/join`,
            body: JSON.stringify({
              senderNickname: assignedNickname,
              type: 'JOIN'
            })
          });
        },
        onStompError: (error) => {
          console.error('WebSocket error:', error);
          setConnected(false);
          setConnecting(false);
        }
      });
      client.activate();
    } catch (error) {
      console.error('Failed to init chat:', error);
      setConnecting(false);
    }
  };

  const disconnectSync = () => {
    if (stompClientRef.current) {
      const currentNickname = nicknameRef.current;
      if (currentNickname) {
        try {
          stompClientRef.current.publish({
            destination: `/app/chat/${GLOBAL_ROOM_ID}/leave`,
            body: JSON.stringify({ senderNickname: currentNickname, type: 'LEAVE' })
          });
        } catch (e) {}
      }
      try {
        if (subscriptionRef.current) {
          subscriptionRef.current.unsubscribe();
        }
        stompClientRef.current.deactivate();
      } catch (e) {}
      stompClientRef.current = null;
      subscriptionRef.current = null;
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !stompClientRef.current || !connected) return;

    stompClientRef.current.publish({
      destination: `/app/chat/${GLOBAL_ROOM_ID}/send`,
      body: JSON.stringify({
        senderNickname: nickname,
        content: inputMessage.trim(),
        type: 'CHAT'
      })
    });
    setInputMessage('');
  };

  const formatTime = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleTimeString('ja-JP', { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="chat-popup">
      {/* Header */}
      <div className="chat-header">
        <span className="chat-title">üí¨ „ÉÅ„É£„ÉÉ„Éà</span>
        <span className="chat-status">
          {connecting ? 'Êé•Á∂ö‰∏≠...' : connected ? `${nickname}` : 'ÂàáÊñ≠'}
        </span>
      </div>

      {/* Messages */}
      <div className="chat-messages">
        {messages.map((msg, index) => {
          if (msg.type === 'JOIN' || msg.type === 'LEAVE') {
            return (
              <div key={msg.id || index} className="chat-system">
                {msg.content}
              </div>
            );
          }
          const isMe = msg.senderNickname === nickname;
          return (
            <div key={msg.id || index} className={`chat-msg ${isMe ? 'mine' : 'other'}`}>
              {!isMe && <span className="msg-sender">{msg.senderNickname}</span>}
              <div className="msg-bubble">
                <span className="msg-text">{msg.content}</span>
                <span className="msg-time">{formatTime(msg.createdAt)}</span>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <form className="chat-input-area" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="„É°„ÉÉ„Çª„Éº„Ç∏„ÇíÂÖ•Âäõ..."
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          className="chat-input"
          disabled={!connected}
          maxLength={500}
        />
        <button
          type="submit"
          className="chat-send"
          disabled={!connected || !inputMessage.trim()}
        >
          ÈÄÅ‰ø°
        </button>
      </form>
    </div>
  );
}

export default ChatPage;
